mapper.select:
select tc.`name` cfg_name,tc.`id` cfg_id,avg(il.pps) pps,max(il.sum_points) sum,max(il.end_time)-min(il.begin_time) cost_iime,1 success_ratio from tsdb_config tc ,import_log il where il.w_id= (select max(id) from tsbm_test_batch where template_id=1 and cfg_id=tc.id) and tc.id in
#{cfgId}
group by tc.id,tc.`name`

select tmp.*, (select clients from tsbm_write_result twr where twr.pps=tmp.pps and tmp.batch_id=twr.batch_id order by clients desc limit 1) clients from (select tc.name cfg_name,tc.id cfg_id,twr.batch_id,max(pps) pps,max(points_sum) sum,min(max_timeout) max_timeout, min(min_timeout) min_timeout,min(avg_timeout) avg_timeout,min(th50_timeout) th50_timeout,min(th95_timeout) th95_timeout from tsdb_config tc ,tsbm_write_result twr where twr.batch_id= (select max(id) from tsbm_test_batch where template_id=#{tmpId} and cfg_id=tc.id) and tc.id in
#{cfgId}
group by tc.id,tc.name,twr.batch_id) tmp

select tc.id cfg_id,twr.clients `key`,twr.pps `value` from tsdb_config tc ,tsbm_write_result twr where twr.batch_id= (select max(id) from tsbm_test_batch where template_id=#{tmpId} and cfg_id=tc.id) and tc.id in
#{cfgId}
order by tc.id

select tmp.*, (select clients from tsbm_read_result twr where twr.qps=tmp.pps and tmp.batch_id=twr.batch_id order by clients desc limit 1) clients from (select tc.name cfg_name,tc.id cfg_id,twr.batch_id,max(qps) pps,max(query_sum) sum,min(max_timeout) max_timeout, min(min_timeout) min_timeout,min(avg_timeout) avg_timeout,min(th50_timeout) th50_timeout,min(th95_timeout) th95_timeout from tsdb_config tc ,tsbm_read_result twr where twr.batch_id= (select max(id) from tsbm_test_batch where template_id=#{tmpId} and cfg_id=tc.id) and tc.id in
#{cfgId}
group by tc.id,tc.name,twr.batch_id) tmp

select tc.id cfg_id,twr.clients `key`,twr.qps `value` from tsdb_config tc ,tsbm_read_result twr where twr.batch_id= (select max(id) from tsbm_test_batch where template_id=#{tmpId} and cfg_id=tc.id) and tc.id in
#{cfgId}
order by tc.id
